import React, { useState, useEffect} from 'react';
import ReactDOM from 'react-dom'; 
import { createLink } from '@meshconnect/web-link-sdk';
import PopupWindow from './PopupWindow'; // Adjust the path based on your project structure

const OpenLinkWebSDK = () => {
    const [linkConnection, setLinkConnection] = useState(null);
    const [linkToken, setLinkToken] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    let popupWindow = null;

    useEffect(() => {
      const fetchLinkToken = async () => {
        setLoading(true);
        setError(null);
        try {
          const response = await fetch('http://localhost:3000/api/linkTokenCall', {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            },
          });

          if (!response.ok) {
            throw new Error('Network response error');
          }

          const result = await response.json();
          setLinkToken(result.linkToken);
        } catch (error) {
          console.error('Error fetching link token:', error);
          setError(error.message);
        } finally {
          setLoading(false);
        }
      };

      fetchLinkToken();

      const link = createLink({
        clientId: "b7007f87-c050-40cf-7777-08dc6f959a47", // Replace with your client ID
        onIntegrationConnected: (data) => {
          console.log('Integration connected:', data);
        },
        onEvent: (event) => {
          console.info('Mesh EVENT', event);
          if (event.type === 'close') {
            console.log('Close event occurred in Mesh modal');
            if (popupWindow) {
              popupWindow.close();
              popupWindow = null;
            }
          }
        },
        onExit: (error) => {
          if (error) {
            console.error('Link exited with error:', error);
          } else {
            console.log('Link exited successfully');
          }
        }
      });
      setLinkConnection(link);

      return () => {
        if (linkConnection) {
          linkConnection.destroy();
        }
      };
    }, []);

    const openPopup = () => {
      if (!linkToken) {
        setError('Link token not fetched yet.');
        return;
      }

      if (popupWindow && !popupWindow.closed) {
        popupWindow.location.reload();
      } else {
        popupWindow = window.open('', 'MeshLinkPopup', 'width=800,height=600');
        if (popupWindow) {
          popupWindow.document.title = 'MeshLinkPopup';
          popupWindow.document.body.innerHTML = '<div id="popup-root"></div>';
          const popupRoot = popupWindow.document.getElementById('popup-root');
          const popupComponent = <PopupWindow linkToken={linkToken} linkConnection={linkConnection} />;
          ReactDOM.render(popupComponent, popupRoot);
        } else {
          setError('Popup blocked. Please enable popups for this site.');
        }
      }
    };

    const handleButtonClick = () => {
      openPopup();
    };

    return (
      <div>
        <button onClick={handleButtonClick} disabled={loading}>
          {loading ? 'Loading...' : 'OpenLink via SDK'}
        </button>
        {error && <p>Error: {error}</p>}
        {linkToken && <p>Link Token: {linkToken}</p>}
      </div>
    );
  };

  export default OpenLinkWebSDK;
