import React, { useState, useEffect } from 'react';
import { createLink } from '@meshconnect/web-link-sdk';

const OpenLinkWebSDK = ({ onLinkTokenFetched }) => {
  const [linkToken, setLinkToken] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchLinkToken = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:3000/api/linkTokenCall', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response error');
      }

      const result = await response.json();
      setLinkToken(result.linkToken);
      onLinkTokenFetched(result.linkToken); // Pass linkToken to parent component (popup window)
    } catch (error) {
      console.error('Error fetching link token:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLinkToken();
  }, []);

  return (
    <div>
      {loading ? <p>Loading...</p> : (
        <>
          {error && <p>Error: {error}</p>}
          {linkToken && <p>Link Token fetched: {linkToken}</p>}
        </>
      )}
    </div>
  );
};

export default OpenLinkWebSDK;
