import React, { useState, useEffect } from 'react';
import { createLink } from '@meshconnect/web-link-sdk';

const PopupWindow = ({ linkToken }) => {
  const [linkConnection, setLinkConnection] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    console.log(linkToken)
    if (linktoken) {
      const link = createLink({
        clientId: "b7007f87-c050-40cf-7777-08dc6f959a47", // Replace with your client ID
        onIntegrationConnected: (data) => {
          console.log('Integration connected:', data);
        },
        onEvent: (event) => {
          console.info('Mesh EVENT', event);
          if (event.type === 'close') {
            console.log('Close event occurred in Mesh modal');
            window.close(); // Close popup window on integration close event
          }
        },
        onExit: (error) => {
          if (error) {
            console.error('Link exited with error:', error);
          } else {
            console.log('Link exited successfully');
          }
        }
      });
      setLinkConnection(link);
    }
  }, [linkToken]);

  const handleOpenLinkClick = async () => {
    console.log(linkToken)
    if (!linkToken) {
      setError('Link token not fetched yet.');
      return;
    }

    try {
      await linkConnection.openLink(linkToken, {
        onSuccess: () => {
          console.log('Link opened successfully in popup');
        },
        onError: (error) => {
          console.error('Error opening link in popup:', error);
          alert('Failed to open link. Please try again.');
        }
      });
    } catch (error) {
      console.error('Error opening link in popup:', error);
      alert('Failed to open link. Please try again.');
    }
  };

  return (
    <div>
      <h1>Popup Window</h1>
      {loading ? <p>Loading...</p> : (
        <>
          {error && <p>Error: {error}</p>}
          {linkToken && (
            <button onClick={handleOpenLinkClick} disabled={loading}>
              Open Link
            </button>
          )}
        </>
      )}
    </div>
  );
};

export default PopupWindow;
