import React, { useState } from 'react';
import { createLink } from '@meshconnect/web-link-sdk';
import PopupWindow from './PopupWindow';

const OpenLinkWebSDK = () => {
  const [linkToken, setLinkToken] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchLinkToken = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:3000/api/linkTokenCall', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response error');
      }

      const result = await response.json();
      setLinkToken(result.linkToken);
    } catch (error) {
      console.error('Error fetching link token:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenPopup = async () => {
    if (!linkToken) {
      await fetchLinkToken();
    }
    // Open the popup window with the linkToken
    const popupWindow = window.open('', 'MeshLinkPopup', 'width=800,height=600');
    if (popupWindow) {
      const link = createLink({
        clientId: "b7007f87-c050-40cf-7777-08dc6f959a47", // Replace with your client ID
        onIntegrationConnected: (data) => {
          console.log('Integration connected:', data);
        },
        onEvent: (event) => {
          console.info('Mesh EVENT', event);
          if (event.type === 'close') {
            console.log('Close event occurred in Mesh modal');
            popupWindow.close(); // Close popup window on integration close event
          }
        },
        onExit: (error) => {
          if (error) {
            console.error('Link exited with error:', error);
          } else {
            console.log('Link exited successfully');
          }
        }
      });

      try {
        await link.openLink(linkToken, {
          container: popupWindow,
          onSuccess: () => {
            console.log('Link opened successfully in popup');
          },
          onError: (error) => {
            console.error('Error opening link in popup:', error);
            alert('Failed to open link. Please try again.');
          }
        });
      } catch (error) {
        console.error('Error opening link in popup:', error);
        alert('Failed to open link. Please try again.');
      }
    } else {
      setError('Popup blocked. Please enable popups for this site.');
    }
  };

  return (
    <div>
      <button onClick={handleOpenPopup} disabled={loading}>
        {loading ? 'Loading...' : 'OpenLink via SDK in Popup'}
      </button>
      {error && <p>Error: {error}</p>}
      {linkToken && <p>Link Token: {linkToken}</p>}
    </div>
  );
};

export default OpenLinkWebSDK;
