// src/components/MyComponent.js
import React, { useState, useEffect } from 'react';
import { createLink } from '@meshconnect/web-link-sdk';

const MakeAPICall = () => {
  const [linkToken, setLinkToken] = useState(null);

  const fetchData = async () => {
    try {
      const response = await fetch('/api/makeAPIcall', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response error');
      }

      const result = await response.json();
      setLinkToken(result.linkToken);
    } catch (error) {
      console.error('Error fetching the API', error);
    }
  };

  useEffect(() => {
    if (linkToken) {
      const link = createLink({
        clientId: process.env.NEXT_PUBLIC_MESH_CLIENTID,
        onIntegrationConnected: (data) => {
          console.log('Integration connected:', data);
        },
        onExit: (error) => {
          if (error) {
            console.error('Link exited with error:', error);
          } else {
            console.log('Link exited successfully');
          }
        }
      });

      // Open the Link UI in an iframe
      const iframe = document.getElementById('mesh-link-iframe');
      iframe.onload = () => {
        link.openLink(linkToken);
      };
    }
  }, [linkToken]);

  return (
    <div>
      <button onClick={fetchData}>Fetch Data</button>
      {linkToken ? (
        <div>
          <h1>Mesh Link UI</h1>
          <iframe
            id="mesh-link-iframe"
            src="about:blank"
            width="100%"
            height="600px"
            frameBorder="0"
          ></iframe>
        </div>
      ) : (
        <div>No data fetched yet.</div>
      )}
    </div>
  );
};

export default MakeAPICall;
