import React, { useState, useEffect } from 'react';

const OpenLink = () => {
  const [iFrameUrl, setIFrameUrl] = useState(null);
  const [showIframe, setShowIframe] = useState(false);
  const [authToken, setAuthToken] = useState(null);

  useEffect(() => {
    const handleMessage = (event) => {
      // Verify the origin of the message
      if (event.origin !== 'http://localhost:3000') {
        return;
      }

      const { data } = event;
      if (data.auth_token) {
        setAuthToken(data.auth_token);
        localStorage.setItem('auth_token', data.auth_token); // Optionally store in local storage
      } else if (data.error) {
        console.error('Error received from iframe:', data.error);
      }
    };

    window.addEventListener('message', handleMessage);

    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/getCatalogLink', {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response error');
      }

      const result = await response.json();
      setIFrameUrl(result.iFrameUrl);
    } catch (error) {
      console.error('Error fetching the API', error);
    }
  };

  const handleOpenIframe = () => {
    if (!iFrameUrl) {
      fetchData();
    }
    setShowIframe(true);
  };

  return (
    <div>
      <button onClick={handleOpenIframe}>Open Mesh</button>
      {showIframe && iFrameUrl && (
        <div>
          <h3>Catalog Link success</h3>
          <iframe src={iFrameUrl} style={{ width: '400px', height: '600px', border: 'none' }} />
        </div>
      )}
      {authToken && (
        <p>Authenticated successfully with token: {authToken}</p>
      )}
    </div>
  );
};

export default OpenLink;
