import React, { useState, useEffect, useRef } from 'react';
import { createLink } from '@meshconnect/web-link-sdk';

const MakeAPICall = () => {
  const [linkToken, setLinkToken] = useState(null);
  const iframeRef = useRef(null);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/makeAPIcall', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response error');
      }

      const result = await response.json();
      setLinkToken(result.linkToken);
    } catch (error) {
      console.error('Error fetching the API', error);
    }
  };

  useEffect(() => {
    const openLinkInIframe = async () => {
      try {
        if (linkToken && iframeRef.current) {
          const link = createLink({
            clientId: process.env.NEXT_PUBLIC_MESH_CLIENTID,
            onIntegrationConnected: (data) => {
              console.log('Integration connected:', data);
            },
            onExit: (error) => {
              if (error) {
                console.error('Link exited with error:', error);
              } else {
                console.log('Link exited successfully');
              }
            }
          });

          // Make sure the iframe is loaded before calling openLink
          iframeRef.current.onload = () => {
            console.log("iframe loaded")
            link.openLink(linkToken, {
              container: iframeRef.current.contentWindow,
            });
          };
        }
      } catch (error) {
        console.error('Error opening link:', error);
      }
    };

    openLinkInIframe();
  }, [linkToken]);

  return (
    <div>
      <button onClick={fetchData}>Fetch Data</button>
      {linkToken ? (
        <div>
          <h3>Link Token success</h3>
          <div style={{ maxHeight: '300px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px' }}>
            <p style={{ whiteSpace: 'pre-wrap' }}>Link Token: {linkToken}</p>
          </div>
          <iframe
            ref={iframeRef}
            title="Mesh Connect Link"
            style={{ width: '500px', height: '500px', border: '1px solid #ccc' }}
          />
        </div>
      ) : (
        <div>No data fetched yet.</div>
      )}
    </div>
  );
};

export default MakeAPICall;
