import React from 'react';

const PopupWindow = () => {
  const handleOpenLinkClick = async () => {
    try {
      await window.opener.openLink(); // Call openLink() function from parent window
    } catch (error) {
      console.error('Error opening link in popup:', error);
      alert('Failed to open link. Please try again.');
    }
  };

  return (
    <div>
      <h1>Popup Window</h1>
      <button onClick={handleOpenLinkClick}>Open Link</button>
    </div>
  );
};

export default PopupWindow;
