// App.js

import React from 'react';
import SendBackgroundMessage from './SendBackgroundMessage';
import MessageDisplay from './MessageDisplay';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h3>Mesh App Example</h3>
      </header>
      <div className="App-content">
        <div className="Section">
          <h4>Send Message to Background</h4>
          <SendBackgroundMessage />
        </div>
        <div className="Section">
          <h4>Messages from Background</h4>
          <MessageDisplay />
        </div>
      </div>
    </div>
  );
}

export default App;
