import React, { useState } from 'react';
import { createLink } from '@meshconnect/web-link-sdk';

const OpenLink = () => {
  const [iFrameUrl, setIFrameUrl] = useState(null);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/cataloglinkCall', {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response error');
      }

      const result = await response.json();
      setIFrameUrl(result.iFrameUrl);
    } catch (error) {
      console.error('Error fetching the API', error);
    }
  };

  const openCatalogPopup = async () => {
    try {
      if (iFrameUrl) {
        const link = createLink({
          clientId: "b7007f87-c050-40cf-7777-08dc6f959a47",
          onIntegrationConnected: (data) => {
            console.log('Integration connected:', data);
          },
          onExit: (error) => {
            if (error) {
              console.error('Link exited with error:', error);
            } else {
              console.log('Link exited successfully');
            }
          }
        });
        const popup = window.open(iFrameUrl, '_blank', 'width=800,height=600');
        if (!popup) {
          throw new Error('Failed to open popup window');
        }
      }
    } catch (error) {
      console.error('Error opening catalog:', error);
    }
  };

  return (
    <div>
      <button onClick={fetchData}>Fetch Catalog Link</button>
      {iFrameUrl ? (
        <div>
          <h3>Catalog Link success</h3>
          <p style={{ whiteSpace: 'pre-wrap' }}>Catalog IFrame URL: {iFrameUrl}</p>
          <button onClick={openCatalogPopup}>Open Catalog</button>
        </div>
      ) : (
        <div>No catalog link fetched yet.</div>
      )}
    </div>
  );
};

export default OpenLink;
