// App.js

import React from 'react';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h3>Mesh App Example</h3>
        <div className="OpenLink">
          <button onClick={sendMessageToBackground}>Send Message to Background</button>
        </div>
      </header>
    </div>
  );
}

function sendMessageToBackground() {
  // Example: Assume you have an event to trigger in background.js
  // You would typically do this through an action from your extension UI
  console.log('Sending message to background.js');
}

export default App;
