import React, { useEffect } from 'react';
import './App.css';
import GetCatalog from './components/GetCatalog';
import OpenLinkWebSDK from './components/OpenLinkWebSDK';

function App() {
  useEffect(() => {
    const desiredWidth = 700;
    const desiredHeight = 700;

    function enforceSize() {
      if (window.outerWidth !== desiredWidth || window.outerHeight !== desiredHeight) {
        window.resizeTo(desiredWidth, desiredHeight);
      }
    }

    enforceSize();

    window.addEventListener('resize', enforceSize);

    // Clean up the event listener on component unmount
    return () => {
      window.removeEventListener('resize', enforceSize);
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h3>Mesh App Example</h3>
        <div className="OpenLink">
          <GetCatalog />
          <OpenLinkWebSDK />
        </div>
      </header>
    </div>
  );
}

export default App;
